import { useEffect, useState } from "react"

const ProfileStatus = (props) => {

    // const localState = {
    //     editMode: 1,
    //     value: 'hi'
    // }

    useEffect(() => {
        // localState.editMode = false
        console.log('Первый рендер')
    }, [])


    const [localState, setState] = useState({ editMode: false })

    const activatedMode = () => {
        // setState( initState => {console.log('Активация'); return { ...initState, editMode: 1}})
        setState(true)
        console.log(localState)
    }

    const disabledMode = (e) => {

        if (e.keyCode === 13) {
            // setState( initState => {console.log('Деактивация'); return { ...initState, editMode: 0}})
            setState(false)
        }
    }

    console.log(localState)

    if (localState.editMode === true) {
        return (<input onKeyDown={setState(false)} value='hello'></input>)
    }
    else {
        return (<span onDoubleClick={setState(true)}>Hello</span>)
    }
}

export default ProfileStatus
