import React from "react"
import * as axios from "axios"
import { connect } from 'react-redux'
import { setUser } from '../../redux/profileReducer'
import Profile from "./Profile"


class ProfileContainer extends React.Component {

    componentDidMount() {
            axios.get(`https://social-network.samuraijs.com/api/1.0/profile/${this.props.currentUser}`)
                .then(response => {
                    this.props.setUser(response.data)
                    console.log(response.data)
                })

    }
    componentWillUnmount() {

    }

    render() {
        if (this.props.profilePage.user.length != 0){
        return <Profile {...this.props} />
        // return <div>Profile</div> }
        }
        else{return <div>Pro</div>}
    }
}

const mapStateToProps = (state) => {
    return {
        profilePage: state.profilePage
    }
}



export default connect(mapStateToProps, { setUser })(ProfileContainer)
