const ADD_MESSAGE = 'ADD-MESSAGE'
const UPDATE_MESSAGE_TEXT = 'UPDATE-MESSAGE-TEXT'

const dialogsReducer = (state, action) => {
    if (action.type === 'ADD-MESSAGE') {
        let text = state.dialogsPage.newMessageValue
        state.dialogsPage.messages.push({ userid: 'me', message: text, image: 'https://www.seekpng.com/png/detail/59-593478_mr-krabs-mr-krabs-png.png' })
        state.dialogsPage.newMessageValue = ''
        return state;
    }

    else if (action.type === 'UPDATE-MESSAGE-TEXT') {
        state.dialogsPage.newMessageValue = action.newText;
        return state;

    }
    else { return state }
}

export const addMessageActionCreator = () => {
    return { type: ADD_MESSAGE }
}

export const updateMessageActionCreator = (text) => {
    return { type: UPDATE_MESSAGE_TEXT, newText: text }
}

export default dialogsReducer
