class AppContainer extends React.Component {

  constructor(props) {
    super(props)
    this.state = { error: null }

  }

  componentDidMount() {
    console.log(this)
    debugger
  }

  componentWillUnmount() {

  }

  static getDerivedStateFromError(error) {
    // Обновить состояние с тем, чтобы следующий рендер показал запасной UI.
    return { hasError: true };
  }

  render() {
    if (this.store.hasError = true){
      return (
        <div>Error</div>
      )
    }
    return (
      <div className='app-wrapper'>
        <Header />
        <Nav sidebar={this.state.sidebar} />
        <div className='app-wrapper-content'>
          <Routes>
            <Route path='/profile/me' element={<ProfileContainer store={props.store} />} />
            <Route path='/dialogs/*' element={<DialogsContainer store={props.store} />} />
            <Route path='/news' element={<News />} />
            <Route path='/music' element={<Music />} />
            <Route path='/settings' element={<Settings />} />
            <Route path='/profile/002' element={<Profile store={props.store} user={props.state.sidebar.users[1]} />} />
            <Route path='/users' element={<UsersContainer store={props.store} />} />
            <Route path='/clock' element={<Clock />} />
          </Routes>
        </div>
      </div>
    )
  }
}

export default AppContainer;
