import React from 'react';
import classes from './MyPosts.module.css'
import Post from './Post/Post';
import { addPostActionCreator, updatePostTextActionCreator } from '../../../redux/profileReducer'

function MyPosts(props) {
    let newPostElem = React.createRef();
    let postElems = props.profilePage.posts.map(p => (<Post message={p.message} likesCount={p.likesCount} avatar={p.avatar} />));

    let onAddPost = () => {
        props.addPost()
    }

    let onUpdatePostText = () => {
        let newText = newPostElem.current.value;
        props.dispatch( updatePostTextActionCreator(newText) )
    }

    return (
        <div className={classes.posts}>
            My posts
            <div><textarea onChange={onUpdatePostText} ref={newPostElem} value={props.profilePage.newPostText} /></div>
            <button onClick={onAddPost}>Add post</button>
            {postElems}
        </div>
    )
}

export default MyPosts
