import { useEffect, useState } from "react"

const ProfileStatus = (props) => {

    const localState = {
        editMode: false,
        value: 'hi'
    }

    useEffect(()=>{
        localState.editMode = false
        console.log(localState)
    },[])


    const [ state , setState ] = useState(localState)

    const activatedMode = () =>{
        setState( state => {
            state = {...state, editMode: true}
            console.log(state)
        })
    }

    console.log(localState.editMode)

    if (localState.editMode == true){
        return (<input value={localState.value}></input>)
    }
    else if (localState.editMode == false) {
        return (<span onDoubleClick={activatedMode}>Hello</span>)
    }
}

export default ProfileStatus
