        // localState.editMode = false
        console.log('Первый рендер')
    },[])


    let [ initState, setState ] = useState(localState)

    const activatedMode = () =>{
        setState( initState => initState.editMode++)
    }

    const disabledMode = (e) =>{

        if (e.keyCode === 13){
        setState( initState => initState.editMode++, console.log(initState))
        }
    }

    console.log(localState)

    if (localState.editMode = 1){
        console.log(localState)
        return (<input onKeyDown={disabledMode} value='hello'></input>)
    }
    else if (localState.editMode = 0) {
        return (<span onDoubleClick={activatedMode}>Hello</span>)
    }
}

export default ProfileStatus
