import { useEffect, useState } from "react"

const ProfileStatus = (props) => {

    const localState = {
        editMode: null,
        value: true
    }

    useEffect(()=>{
        state.editMode = false
    },[])


    const [ state , setState ] = useState(localState)

    const activatedMode = () =>{
        setState( state => {
            state.editMode = true
            console.log(state)
        })
    }

    return (
        <>
            {!localState.editMode
            ?<span onDoubleClick={ activatedMode }>Hello</span>
            :<input  value='hello' />}
        </>
    )
}

export default ProfileStatus
