const SHOW_MORE = 'SHOW-MORE'
const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SETUSERS = 'SETUSERS'

const initialState = {
    users: [
        { id: 1, followed: true, avatar: 'https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fimagesvc.meredithcorp.io%2Fv3%2Fmm%2Fimage%3Furl%3Dhttps%253A%252F%252Fstatic.onecms.io%252Fwp-content%252Fuploads%252Fsites%252F13%252F2015%252F03%252F04%252FSquidward_Design_2.jpg&q=85', fullname: 'Squid Ward', status: 'Hello world', location: { country: 'Ocean', city: 'Bikini' } },
        { id: 2, followed: false, avatar: 'https://all-t-shirts.ru/goods_images/ru110593II000eed5c5dc8999b359b5e0e6cd786bae4b.jpg', fullname: 'Squid Ward', status: 'World Hello', location: { country: 'Ocean', city: 'Bikini' } }
    ]
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FOLLOW':
            return {
                ...state, users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return (
                            { ...u, followed: true }
                        )
                    }
                    return u;
                })
            }
        case 'UNFOLLOW':
            return {
                ...state, users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return (
                            { ...u, followed: false }
                        )
                    }
                    return u;
                })
            }
        case 'SETUSERS':
            return {
                ...state, users: [...state.users, ...action.users]
            }
        default:
            return state;
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId })
export const setUsersAC = (users) => ({ type: SETUSERS, users })

export default usersReducer
