import React from 'react';
import { addMessageActionCreator, updateMessageActionCreator } from '../../redux/dialogsReducer'
import Dialogs from './Dialogs';
import {connect} from 'react-redux'


// function DialogsContainer(props) {
//   let state = props.store.getState()
//   let addMessage = () => {
//     props.store.dispatch( addMessageActionCreator() )
//   }


//   let updateMessage = (text) =>{
//     props.store.dispatch( updateMessageActionCreator(text) )
//   }

//   return <Dialogs dialogsPage={state.dialogsPage} addMess={addMessage} updateMess={updateMessage}/>
// }

const mapStateToProps = (state) => { dialogsPage: state.dialogsPage}

const mapDispatchToProps = (dispatch) => {
  return {
    addMess: () => dispatch( addMessageActionCreator() ),
    updateMess: (text) => dispatch( updateMessageActionCreator(text) )
  }
}

const DialogsContainer = connect(mapStateToProps, mapDispatchToProps)(Dialogs)

export default DialogsContainer
