import React from "react"
import { useDispatch } from "react-redux"
import { useFormik} from 'formik'
import { authThunk } from "../../redux/async/authThunk"
import * as Yup from 'yup'

const Login = (props) => {

    const dispatch = useDispatch()

    // const loginSchema = Yup.object().shape({
    //     email: Yup.string()
    //     // .email('Invalid email')
    //     .required('Please enter email')
    // })

    const formik = useFormik({
        initialValues: {
            email: '',
            password: '',
            rememberMe: false
        },
        validationSchema: Yup.object().shape({
            email: Yup.string()
            .email('Invalid email')
            .required('Please enter email')
        }),
        onSubmit: values =>{
            console.log(values)
            dispatch(authThunk(values))
        }
    }

    )

    return (
        <div>
            Login
            <form onSubmit={formik.handleSubmit}>
                <div><input id='email' name='email' type='email' placeholder='email' value={formik.values.email} onChange={formik.handleChange} /></div>
                {formik.touched.email && formik.errors.email ? (
         <span className="error">{formik.errors.email}</span>
     ) : null}
                <div><input id='password' name='password' type='password' placeholder='password' value={formik.values.password} onChange={formik.handleChange} /></div>
                <div><input id='rememberMe' name='rememberMe' type='checkbox'/>remember me</div>
                <button type='submit'>sign in</button>
            </form>
        </div>
    )

}

export default Login
