import React from "react"
import { useDispatch } from "react-redux"
import { useFormik } from 'formik'
import { loginThunk } from "../../redux/async/authThunk"
import { useForm } from 'react-hook-form'
import { useEffect } from "react"
import { NavLink, useNavigate } from "react-router-dom"
import * as Yup from 'yup'
import classes from './Login.module.css'
import Profile from "../Profile/Profile"

const Login = (props) => {

    const dispatch = useDispatch()

    const navigate = useNavigate()

    const {
        register,
        formState: {
            errors,
            isValid
        },
        handleSubmit,
        reset //очистка формы после отправки данных
    } = useForm({ mode: 'onBlur' })

    const onSubmit = (data) => {
        console.log(data)
        dispatch(loginThunk(data))
        reset()
    }

    // useEffect(() => {
    //     console.log(props.state.auth.isAuth)
    //     console.log(props.state.auth.userId)
    //     if (props.state.auth.isAuth = true && props.state.auth.userId != null) {
    //         console.log('sds')
    //         navigate(`/profile/${props.state.auth.userId}`)
    //     }
    // }, [])

    // if (!props.state.auth.isAuth) {

        return (
            <>
            {/* // {(props.state.auth.isAuth? <div>Залогинен</div>)} */}
                <h1>Login</h1>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <label>E-mail:<br />
                        <input type='email' {...register('email',
                            {
                                required: 'Поле обязательное к заполнению',
                                minLength: { value: 5, message: 'Минимум 5 символов' }
                            })} />
                        {errors?.email && <div className={classes.error}>{errors?.email?.message || 'Общая ошибка'}</div>}
                    </label><br />
                    <label>password:<br />
                        <input type='password' {...register('password',
                            {
                                required: 'Заполните поле пароля',
                                minLength: { value: 6, message: 'Длина пароля минимум 6 символов' }
                            })} />
                        {errors?.password && <div className={classes.error}>{errors?.password?.message}</div>}
                    </label>
                    <br /><input type='checkbox' {...register('rememberMe')} />Remember me?

                    <br /><input type='submit' disabled={!isValid} />
                </form>
            </>
        )
    }

    // else {
    //     navigate(`../profile/${props.state.auth.userId}`)

    //     return <Profile profilePage={props.state.profilePage}/>
    // }
// }

export default Login
