import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updatePostText } from '../../../redux/profileReducer';
import classes from './MyPosts.module.css'
import Post from './Post/Post';

function MyPosts(props) {

    // console.log(props)
    // debugger

    let newPostElem = React.createRef();
    let postElems = props.profilePage.posts.map(p => (<Post key={p.id} message={p.message} likesCount={p.likesCount} avatar={p.avatar} />));

    let onAddPost = () => {
        props.addPost()
    }

    // let onUpdatePostText = () => {
    //     let newText = newPostElem.current.value;
    //     props.updatePostText(newText) 
    // }

    const dispatch = useDispatch()

    const onUpdatePostText = () => {
        let newText = newPostElem.current.value;
        dispatch(updatePostText(newText))
    }

    return (
        <div className={classes.posts}>
            My posts
            <div><textarea onChange={onUpdatePostText} ref={newPostElem} value={props.newPostText} /></div>
            <button onClick={onAddPost}>Add post</button>
            {postElems}
        </div>
    )
}

export default MyPosts
