import React from "react"
import * as axios from "axios"
import classes from './Users.module.css'
import { showMoreAC } from "../../redux/usersReducer";


class User extends React.Component {

    constructor(props) {
        super(props);
        this.onShowMore = () => {
            this.props.showMore()
        }

    }

    // continue() {
    //     console.log('...')
    // }

    componentDidMount() {
        if (this.props.users.length === 0) {
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
                .then(response => { this.props.setUsers(response.data.items) })
        }
        // var but = <button onClick={this.props.showMore}>show more</button>
    }

    componentWillUnmount() {
        this.props.users.length = 0
    }

    onShowMore() {
        // let heh = props
        // console.log(heh)
    }

    onSetPage(page) {
        () => {this.props.setPage(page)}
    }

    render() {
        let header = 'Users'
        let but = <button>show more</button>
        let pages = []

        for (let i = 1; i <= Math.ceil(this.props.pageCount / this.props.pageSize); i++){
            pages.push(i)
        }


        if (this.props.users.length === 0){
            header = 'Загрузка...'
            but = ''
        }

        return <div>{header}{
            this.props.users.map(u => (
                <div key={u.id} className={classes.users}>
                    <div className={classes.user}>
                        <img src={u.photos.small != null
                            ? u.photos.small
                            : "https://www.seekpng.com/png/full/356-3562377_personal-user.png"} /><br />
                        <div>
                            {u.followed
                                ? <button onClick={() => { this.props.unfollow(u.id) }}>unfollow</button>
                                : <button onClick={() => { this.props.follow(u.id) }}>follow</button>}
                        </div>
                    </div>

                    <div className={classes.info}>
                        <div className={classes.name}>
                            <div>{u.name}</div>
                            <div>{u.status != null
                                ? u.status
                                : 'Here need a status'}</div>
                        </div>
                        <div className={classes.location}>
                            <div>{"u.location.country"}</div>
                            <div>{"u.location.city"}</div>
                        </div>
                    </div>
                </div>))
        }
            {but}

        {pages.map( p => {return <span onClick={() => { this.props.setPage(p) }}className={this.props.currentPage === p && classes.selectedPage}>{p}</span>})}

        </div>
    }
}

export default User
